package com.nepal.andoriddemo.activities;import androidx.appcompat.app.AppCompatActivity;import androidx.appcompat.widget.Toolbar;import android.os.Bundle;import android.view.View;import com.nepal.andoriddemo.R;import com.nepal.andoriddemo.helpers.AppActivity;public class SearchActivity extends AppActivity implements View.OnClickListener{    private Toolbar toolbar;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_search);        initializeView();        initializeListeners();        setUpToolbar();    }    @Override    protected void initializeView() {        toolbar = findViewById(R.id.toolbar);    }    @Override    protected void initializeListeners() {    }    private void setUpToolbar() {        setSupportActionBar(toolbar);        toolbar.setTitle("Hello");        if (getSupportActionBar() != null){            getSupportActionBar().setDisplayHomeAsUpEnabled(true);            getSupportActionBar().setDisplayShowHomeEnabled(true);        }    }    @Override    public void onBackPressed() {        super.onBackPressed();        finish();    }    @Override    public void onClick(View view) {        switch (view.getId()){            case R.id.home:                finish();                break;        }    }}