package com.nepal.andoriddemo.activities;import androidx.annotation.NonNull;import androidx.appcompat.app.AppCompatActivity;import androidx.appcompat.widget.Toolbar;import androidx.recyclerview.widget.RecyclerView;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.nepal.andoriddemo.R;import com.nepal.andoriddemo.helpers.AppActivity;import com.nepal.andoriddemo.helpers.AppRecyclerViewAdapter;public class SearchActivity extends AppActivity implements View.OnClickListener{    private Toolbar toolbar;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_search);        initializeView();        initializeListeners();        setUpToolbar();    }    @Override    protected void initializeView() {        toolbar = findViewById(R.id.toolbar);    }    @Override    protected void initializeListeners() {    }    private void setUpToolbar() {        setSupportActionBar(toolbar);        toolbar.setTitle("Hello");        if (getSupportActionBar() != null){            getSupportActionBar().setDisplayHomeAsUpEnabled(true);            getSupportActionBar().setDisplayShowHomeEnabled(true);        }    }    @Override    public void onBackPressed() {        super.onBackPressed();        finish();    }    @Override    public void onClick(View view) {        switch (view.getId()){            case R.id.home:                finish();                break;        }    }    public class NotificationRecyclerViewAdapter extends AppRecyclerViewAdapter{        @Override        public void add(Object object) {        }        @Override        public void clear() {        }        @NonNull        @Override        public RecyclerView.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {            View itemView = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_notification_content, parent, false);            return new VHNotificationItem(itemView);        }        @Override        public void onBindViewHolder(@NonNull RecyclerView.ViewHolder holder, int position) {            VHNotificationItem vhNotificationItem = (VHNotificationItem) holder;            vhNotificationItem.notificationTitle.setText("hello");            vhNotificationItem.notificationDescription.setText("description from hello");        }        @Override        public int getItemCount() {            return 10;        }        private class VHNotificationItem extends RecyclerView.ViewHolder {            private TextView notificationTitle;            private TextView notificationDescription;            public VHNotificationItem(View itemView) {                super(itemView);                notificationTitle = itemView.findViewById(R.id.notification_title);                notificationDescription = itemView.findViewById(R.id.notification_description);            }        }    }}