package com.nepal.andoriddemo.activities;import androidx.annotation.NonNull;import androidx.appcompat.app.AppCompatActivity;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import android.app.Notification;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.nepal.andoriddemo.R;import com.nepal.andoriddemo.helpers.AppActivity;import com.nepal.andoriddemo.helpers.AppRecyclerViewAdapter;import com.nepal.andoriddemo.helpers.DefineClassType;import com.nepal.andoriddemo.models.Notifications;public class TestActivity extends AppActivity {    private RecyclerView recyclerView;    private NotificationRecyclerViewAdapter notificationRecyclerViewAdapter;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_test);        initializeView();        initializeListeners();        prepareRecyclerView();    }    @Override    protected void initializeView() {        recyclerView = findViewById(R.id.recycler_view_1);    }    @Override    protected void initializeListeners() {    }    private void prepareRecyclerView() {        notificationRecyclerViewAdapter = new NotificationRecyclerViewAdapter();        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(TestActivity.this, RecyclerView.VERTICAL, false);        recyclerView.setAdapter(notificationRecyclerViewAdapter);        recyclerView.setLayoutManager(linearLayoutManager);    }    public class NotificationRecyclerViewAdapter extends AppRecyclerViewAdapter {        private Notifications notification;        @Override        public void add(Object object) {        }        @Override        public void clear() {        }        @NonNull        @Override        public RecyclerView.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {            View itemView = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_notification_content, parent, false);            return new VHNotificationItem(itemView);        }        @Override        public void onBindViewHolder(@NonNull RecyclerView.ViewHolder holder, int position) {            VHNotificationItem vhNotificationItem = (VHNotificationItem) holder;            Notifications.Datum data = notification.getData().get(position);            vhNotificationItem.notificationTitle.setText(data.getName());            vhNotificationItem.notificationDescription.setText("description from hello");        }        @Override        public int getItemCount() {            return 10;        }        private class VHNotificationItem extends RecyclerView.ViewHolder {            private TextView notificationTitle;            private TextView notificationDescription;            public VHNotificationItem(View itemView) {                super(itemView);                notificationTitle = itemView.findViewById(R.id.notification_title);                notificationDescription = itemView.findViewById(R.id.notification_description);            }        }    }}